// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

/**
 * @title IConsciousnessIdentity
 * @notice Interface for the Consciousness Identity contract
 */
interface IConsciousnessIdentity {
    enum VerificationLevel {
        NONE,
        BASIC,
        EXECUTIVE,
        ENTERPRISE,
        CONSCIOUSNESS_VERIFIED,
        THOUGHT_LEADER
    }
    
    enum CredentialType {
        LEADERSHIP_ASSESSMENT,
        EXECUTIVE_EXPERIENCE,
        CONSCIOUSNESS_SCORE,
        STRATEGIC_THINKING,
        CRISIS_MANAGEMENT,
        TEAM_LEADERSHIP,
        INNOVATION_CAPACITY,
        ETHICAL_DECISION_MAKING,
        WISDOM_APPLICATION,
        TRANSFORMATION_LEADERSHIP
    }
    
    // Events
    event IdentityCreated(address indexed user, VerificationLevel level);
    event IdentityUpdated(address indexed user, VerificationLevel level);
    event CredentialIssued(address indexed user, CredentialType credType, address indexed issuer);
    event CredentialVerified(address indexed user, CredentialType credType, address indexed verifier);
    event AttestationCreated(bytes32 indexed attestationId, address indexed subject, address indexed verifier);
    event ConsciousnessScoreUpdated(address indexed user, uint256 newScore);
    event CredentialShared(address indexed user, address indexed viewer, CredentialType credType);
    event PrivacySettingsUpdated(address indexed user);
    event VerificationLevelUpgraded(address indexed user, VerificationLevel oldLevel, VerificationLevel newLevel);
    
    // Core functions
    function createIdentity(string memory publicProfile, bytes32 privateProfileHash, bool isEnterpriseAccount) external;
    function issueCredential(address user, CredentialType credType, uint256 score, string memory metadataURI, bytes32 proofHash) external;
    function verifyCredential(address user, CredentialType credType, string memory evidence) external;
    function createAttestation(address subject, VerificationLevel level, string memory evidence, bytes memory signature) external returns (bytes32);
    function updateConsciousnessScore(address user, uint256 newScore, string memory assessmentData) external;
    function shareCredential(address viewer, CredentialType credType, bool allowAccess) external;
    function updatePrivacySettings(bool allowPublicProfile, bool allowCredentialSharing, bool allowConsciousnessScoring, bool allowEnterpriseAccess) external;
    function setCredentialVisibility(CredentialType credType, bool isPublic) external;
    
    // View functions
    function getIdentity(address user) external view returns (VerificationLevel, uint256, uint256, string memory, bool, uint256, uint256);
    function getCredential(address user, CredentialType credType) external view returns (address, uint256, uint256, uint256, bool, bool, string memory);
    function canViewCredential(address viewer, address user, CredentialType credType) external view returns (bool);
    function verifyAttestation(bytes32 attestationId) external view returns (bool);
    
    // Admin functions
    function setTrustedIssuer(address issuer, bool trusted) external;
}

contracts/interfaces/ILeadershipSubscrip.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

/**
 * @title ILeadershipSubscription
 * @notice Interface for the Leadership Subscription contract
 */
interface ILeadershipSubscription {
    enum SubscriptionTier {
        NONE,
        BASIC,
        PREMIUM,
        ENTERPRISE
    }
    
    struct TierConfig {
        uint256 monthlyPrice;
        uint256 annualPrice;
        uint256 maxAIQueries;
        uint256 maxAssessments;
        uint256 votingPower;
        bool hasAdvancedAnalytics;
        bool hasPersonalCoaching;
        bool hasAPIAccess;
        bool hasWhiteLabel;
        string[] includedFeatures;
        uint256 consciousnessRequirement;
        bool isActive;
    }
    
    struct Subscription {
        SubscriptionTier tier;
        uint256 startTime;
        uint256 endTime;
        bool isAnnual;
        bool autoRenewal;
        uint256 lastPayment;
        uint256 totalPaid;
        address paymentToken;
        bool isActive;
        uint256 votingPowerAllocated;
    }
    
    // Events
    event SubscriptionCreated(address indexed user, SubscriptionTier tier, bool isAnnual, uint256 endTime);
    event SubscriptionRenewed(address indexed user, SubscriptionTier tier, uint256 newEndTime);
    event SubscriptionUpgraded(address indexed user, SubscriptionTier oldTier, SubscriptionTier newTier);
    event SubscriptionCancelled(address indexed user, SubscriptionTier tier);
    event AutoRenewalToggled(address indexed user, bool enabled);
    event VotingPowerAllocated(address indexed user, uint256 power);
    
    // Core functions
    function subscribe(SubscriptionTier tier, bool isAnnual, address paymentToken, bool enableAutoRenewal) external;
    function upgradeSubscription(SubscriptionTier newTier) external;
    function processAutoRenewal(address user) external;
    function cancelSubscription() external;
    function toggleAutoRenewal(bool enabled) external;
    
    // View functions
    function hasFeatureAccess(address user, string memory feature) external view returns (bool);
    function isSubscriptionActive(address user) external view returns (bool);
    function getVotingPower(address user) external view returns (uint256);
    function getSubscription(address user) external view returns (SubscriptionTier, uint256, uint256, bool, bool, uint256, bool, uint256);
    function tierConfigs(SubscriptionTier tier) external view returns (TierConfig memory);
    function supportedPaymentTokens(address token) external view returns (bool);
    
    // Admin functions
    function updateTierConfig(SubscriptionTier tier, uint256 monthlyPrice, uint256 annualPrice, uint256 maxAIQueries, uint256 maxAssessments, uint256 votingPower, bool hasAdvancedAnalytics, bool hasPersonalCoaching, bool hasAPIAccess, bool hasWhiteLabel, uint256 consciousnessRequirement, bool isActive) external;
    function setSupportedPaymentToken(address token, bool supported) external;
}

