            // length guarantees that this does not overflow, and value is at most type(uint160).max
            (bool s, uint256 v) = _tryParseHexUintUncheckedBounds(input, begin, end);
            return (s, address(uint160(v)));
        } else {
            return (false, address(0));
        }
    }

    function _tryParseChr(bytes1 chr) private pure returns (uint8) {
        uint8 value = uint8(chr);

        // Try to parse `chr`:
        // - Case 1: [0-9]
        // - Case 2: [a-f]
        // - Case 3: [A-F]
        // - otherwise not supported
        unchecked {
            if (value > 47 && value < 58) value -= 48;
            else if (value > 96 && value < 103) value -= 87;
            else if (value > 64 && value < 71) value -= 55;
            else return type(uint8).max;
        }

        return value;
    }

    /**
     * @dev Escape special characters in JSON strings. This can be useful to prevent JSON injection in NFT metadata.
     *
     * WARNING: This function should only be used in double quoted JSON strings. Single quotes are not escaped.
     *
     * NOTE: This function escapes all unicode characters, and not just the ones in ranges defined in section 2.5 of
     * RFC-4627 (U+0000 to U+001F, U+0022 and U+005C). ECMAScript's `JSON.parse` does recover escaped unicode
     * characters that are not in this range, but other tooling may provide different results.
     */
    function escapeJSON(string memory input) internal pure returns (string memory) {
        bytes memory buffer = bytes(input);
        bytes memory output = new bytes(2 * buffer.length); // worst case scenario
        uint256 outputLength = 0;

        for (uint256 i; i < buffer.length; ++i) {
            bytes1 char = bytes1(_unsafeReadBytesOffset(buffer, i));
            if (((SPECIAL_CHARS_LOOKUP & (1 << uint8(char))) != 0)) {
                output[outputLength++] = "\\";
                if (char == 0x08) output[outputLength++] = "b";
                else if (char == 0x09) output[outputLength++] = "t";
                else if (char == 0x0a) output[outputLength++] = "n";
                else if (char == 0x0c) output[outputLength++] = "f";
                else if (char == 0x0d) output[outputLength++] = "r";
                else if (char == 0x5c) output[outputLength++] = "\\";
                else if (char == 0x22) {
                    // solhint-disable-next-line quotes
                    output[outputLength++] = '"';
                }
            } else {
                output[outputLength++] = char;
            }
        }
        // write the actual length and deallocate unused memory
        assembly ("memory-safe") {
            mstore(output, outputLength)
            mstore(0x40, add(output, shl(5, shr(5, add(outputLength, 63)))))
        }

        return string(output);
    }

    /**
     * @dev Reads a bytes32 from a bytes array without bounds checking.
     *
     * NOTE: making this function internal would mean it could be used with memory unsafe offset, and marking the
     * assembly block as such would prevent some optimizations.
     */
    function _unsafeReadBytesOffset(bytes memory buffer, uint256 offset) private pure returns (bytes32 value) {
        // This is not memory safe in the general case, but all calls to this private function are within bounds.
        assembly ("memory-safe") {
            value := mload(add(add(buffer, 0x20), offset))
        }
    }
}

contracts/AIServicePayment.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title AIServicePayment
 * @notice Manages payments for AI-enhanced consciousness features
 * @dev Supports multiple tokens, usage-based billing, and automatic deductions
 */
contract AIServicePayment is AccessControl, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;
    
    bytes32 public constant SERVICE_MANAGER_ROLE = keccak256("SERVICE_MANAGER_ROLE");
    bytes32 public constant BILLING_ADMIN_ROLE = keccak256("BILLING_ADMIN_ROLE");
    bytes32 public constant REFUND_MANAGER_ROLE = keccak256("REFUND_MANAGER_ROLE");
    
    // Service types
    enum ServiceType {
        CONSCIOUSNESS_ANALYSIS,
        STRATEGIC_INSIGHTS,
        DECISION_SYNTHESIS,
        PATTERN_RECOGNITION,
        WISDOM_INTEGRATION,
        CRISIS_MANAGEMENT,
        LEADERSHIP_COACHING,
        REFLECTION_GUIDANCE
    }
    
    // Payment structures
    struct ServiceConfig {
        uint256 baseCostPerQuery;
        uint256 maxQueriesPerDay;
        uint256 discountForStakers;
        bool isActive;
        string serviceName;
        uint256 minimumStakeRequired; // Minimum gPFORK tokens (staked PFORK) for access
    }
    
    struct UserAccount {
        mapping(address => uint256) tokenBalances; // Pre-funded balances for each token
        mapping(ServiceType => uint256) dailyUsage;
        mapping(ServiceType => uint256) lastUsageReset;
        mapping(ServiceType => uint256) totalSpent;
        uint256 dailySpendingLimit;
        uint256 totalSpendingCap;
        uint256 accountCreated;
        bool isActive;
        uint256 refundableAmount;
    }
    
    struct PaymentRecord {
        address user;
        ServiceType serviceType;
        address paymentToken;
        uint256 amount;
        uint256 timestamp;
        bytes32 sessionId;
        bool wasRefunded;
    }
    
    // Supported tokens
    mapping(address => bool) public supportedTokens;
    mapping(address => string) public tokenNames;
    mapping(address => uint8) public tokenDecimals;
    
    // Service configurations
    mapping(ServiceType => ServiceConfig) public serviceConfigs;
    
    // User accounts and payments
    mapping(address => UserAccount) public userAccounts;
    mapping(bytes32 => PaymentRecord) public paymentRecords;
    
    // Platform settings
    IERC20 public pforkToken;
    IERC20 public gPforkToken;
    address public treasuryAddress;
    uint256 public platformFeePercentage = 500; // 5% in basis points
    uint256 public constant MAX_DAILY_QUERIES = 1000;
    uint256 public constant MAX_SPENDING_CAP = 10000 * 10**18; // 10,000 tokens max
    
    // State tracking
    uint256 public totalPaymentsProcessed;
    uint256 public totalRefundsIssued;
    mapping(ServiceType => uint256) public serviceUsageStats;
    
    // Events
    event ServicePayment(address indexed user, ServiceType indexed serviceType, address token, uint256 amount, bytes32 sessionId);
    event AccountFunded(address indexed user, address indexed token, uint256 amount);
    event RefundIssued(address indexed user, bytes32 indexed paymentId, uint256 amount);
    event ServiceConfigUpdated(ServiceType indexed serviceType, uint256 newCost, bool isActive);
    event DailyLimitUpdated(address indexed user, uint256 newLimit);
    event TokenSupport(address indexed token, bool supported);
    event EmergencyWithdrawal(address indexed token, uint256 amount);
    
    constructor(
        address _pforkToken,
        address _gPforkToken,
        address _treasuryAddress
    ) {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(SERVICE_MANAGER_ROLE, msg.sender);
        _grantRole(BILLING_ADMIN_ROLE, msg.sender);
        _grantRole(REFUND_MANAGER_ROLE, msg.sender);
        
        pforkToken = IERC20(_pforkToken);
        gPforkToken = IERC20(_gPforkToken);
        treasuryAddress = _treasuryAddress;
        
        _initializeServices();
        _setupSupportedTokens();
    }
    
    /**
     * @notice Fund user account with tokens for AI service payments
     * @param token Token address to fund with
     * @param amount Amount of tokens to fund
     */
    function fundAccount(address token, uint256 amount) external nonReentrant whenNotPaused {
        require(supportedTokens[token], "Token not supported");
        require(amount > 0, "Amount must be positive");
        
        // Create account if doesn't exist
        if (userAccounts[msg.sender].accountCreated == 0) {
            userAccounts[msg.sender].accountCreated = block.timestamp;
            userAccounts[msg.sender].isActive = true;
            userAccounts[msg.sender].dailySpendingLimit = MAX_SPENDING_CAP / 100; // 1% of max as default
        }
        
        // Transfer tokens to contract
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount);
        
        // Update user balance
        userAccounts[msg.sender].tokenBalances[token] += amount;
        
        emit AccountFunded(msg.sender, token, amount);
    }
    
    /**
     * @notice Fund account with ETH
     */
    function fundAccountETH() external payable nonReentrant whenNotPaused {
        require(msg.value > 0, "Must send ETH");
        require(supportedTokens[address(0)], "ETH not supported"); // address(0) represents ETH
        
        // Create account if doesn't exist
        if (userAccounts[msg.sender].accountCreated == 0) {
            userAccounts[msg.sender].accountCreated = block.timestamp;
            userAccounts[msg.sender].isActive = true;
            userAccounts[msg.sender].dailySpendingLimit = MAX_SPENDING_CAP / 100;
        }
        
        // Update ETH balance
        userAccounts[msg.sender].tokenBalances[address(0)] += msg.value;
        
        emit AccountFunded(msg.sender, address(0), msg.value);
    }
    
    /**
     * @notice Process payment for AI service usage
     * @param user User requesting the service
     * @param serviceType Type of AI service
     * @param paymentToken Token to pay with
     * @param sessionId Unique session identifier
     */
    function processServicePayment(
        address user,
        ServiceType serviceType,
        address paymentToken,
        bytes32 sessionId
    ) external onlyRole(SERVICE_MANAGER_ROLE) nonReentrant whenNotPaused returns (bool success) {
        require(userAccounts[user].isActive, "User account not active");
        require(supportedTokens[paymentToken], "Payment token not supported");
        require(serviceConfigs[serviceType].isActive, "Service not active");
        
        // Check daily usage limits
        _resetDailyUsageIfNeeded(user, serviceType);
        require(
            userAccounts[user].dailyUsage[serviceType] < serviceConfigs[serviceType].maxQueriesPerDay,
            "Daily query limit exceeded"
        );
        
        // Calculate service cost with discounts
        uint256 cost = _calculateServiceCost(user, serviceType);
        
        // Check spending limits
        require(cost <= userAccounts[user].dailySpendingLimit, "Exceeds daily spending limit");
        require(
            userAccounts[user].totalSpent[serviceType] + cost <= userAccounts[user].totalSpendingCap,
            "Exceeds total spending cap"
        );
        
        // Check balance and process payment
        require(userAccounts[user].tokenBalances[paymentToken] >= cost, "Insufficient balance");
        
        // Deduct from user balance
        userAccounts[user].tokenBalances[paymentToken] -= cost;
        
        // Update usage tracking
        userAccounts[user].dailyUsage[serviceType]++;
        userAccounts[user].totalSpent[serviceType] += cost;
        serviceUsageStats[serviceType]++;
        totalPaymentsProcessed++;
        
        // Calculate platform fee
        uint256 platformFee = (cost * platformFeePercentage) / 10000;
        uint256 netAmount = cost - platformFee;
        
        // Create payment record
        PaymentRecord storage payment = paymentRecords[sessionId];
        payment.user = user;
        payment.serviceType = serviceType;
        payment.paymentToken = paymentToken;
        payment.amount = cost;
        payment.timestamp = block.timestamp;
        payment.sessionId = sessionId;
        payment.wasRefunded = false;
        
        // Transfer to treasury
        if (paymentToken == address(0)) {
            // ETH payment
            (bool sent, ) = payable(treasuryAddress).call{value: netAmount}("");
            require(sent, "ETH transfer failed");
        } else {
            // ERC20 payment
            IERC20(paymentToken).safeTransfer(treasuryAddress, netAmount);
        }
        
        emit ServicePayment(user, serviceType, paymentToken, cost, sessionId);
        return true;
    }
    
    /**
     * @notice Issue refund for a service payment
     * @param paymentId Payment session ID to refund
     * @param reason Refund reason
     */
    function issueRefund(bytes32 paymentId, string memory reason) 
        external 
        onlyRole(REFUND_MANAGER_ROLE) 
        nonReentrant 
    {
        PaymentRecord storage payment = paymentRecords[paymentId];
        require(payment.user != address(0), "Payment not found");
        require(!payment.wasRefunded, "Already refunded");
        require(block.timestamp <= payment.timestamp + 7 days, "Refund period expired");
        
        payment.wasRefunded = true;
        
        // Add to refundable amount
        userAccounts[payment.user].refundableAmount += payment.amount;
        totalRefundsIssued++;
        
        emit RefundIssued(payment.user, paymentId, payment.amount);
    }
    
    /**
     * @notice Withdraw refundable amount
     * @param token Token to withdraw
     */
    function withdrawRefund(address token) external nonReentrant {
        uint256 refundAmount = userAccounts[msg.sender].refundableAmount;
        require(refundAmount > 0, "No refund available");
        require(supportedTokens[token], "Token not supported");
        
        userAccounts[msg.sender].refundableAmount = 0;
        
        if (token == address(0)) {
            // ETH refund
            (bool sent, ) = payable(msg.sender).call{value: refundAmount}("");
            require(sent, "ETH transfer failed");
        } else {
            // ERC20 refund
            IERC20(token).safeTransfer(msg.sender, refundAmount);
        }
    }
    
    /**
     * @notice Withdraw unused account balance
     * @param token Token to withdraw
     * @param amount Amount to withdraw
     */
    function withdrawAccountBalance(address token, uint256 amount) external nonReentrant {
        require(supportedTokens[token], "Token not supported");
        require(userAccounts[msg.sender].tokenBalances[token] >= amount, "Insufficient balance");
        
        userAccounts[msg.sender].tokenBalances[token] -= amount;
        
        if (token == address(0)) {
            // ETH withdrawal
            (bool sent, ) = payable(msg.sender).call{value: amount}("");
            require(sent, "ETH transfer failed");
        } else {
            // ERC20 withdrawal
            IERC20(token).safeTransfer(msg.sender, amount);
        }
    }
    
    /**
     * @notice Update service configuration
     */
    function updateServiceConfig(
        ServiceType serviceType,
        uint256 baseCost,
        uint256 maxQueries,
        uint256 discount,
        bool isActive,
        string memory serviceName,
        uint256 minimumStake
    ) external onlyRole(SERVICE_MANAGER_ROLE) {
        ServiceConfig storage config = serviceConfigs[serviceType];
        config.baseCostPerQuery = baseCost;
        config.maxQueriesPerDay = maxQueries;
        config.discountForStakers = discount;
        config.isActive = isActive;
        config.serviceName = serviceName;
        config.minimumStakeRequired = minimumStake;
        
        emit ServiceConfigUpdated(serviceType, baseCost, isActive);
    }
    
    /**
     * @notice Set user daily spending limit
     */
    function setUserDailyLimit(address user, uint256 newLimit) 
        external 
        onlyRole(BILLING_ADMIN_ROLE) 
    {
        require(newLimit <= MAX_SPENDING_CAP, "Exceeds maximum cap");
        userAccounts[user].dailySpendingLimit = newLimit;
        emit DailyLimitUpdated(user, newLimit);
    }
    
    /**
     * @notice Add or remove token support
     */
    function setSupportedToken(address token, bool supported, string memory name, uint8 decimals) 
        external 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        supportedTokens[token] = supported;
        if (supported) {
            tokenNames[token] = name;
            tokenDecimals[token] = decimals;
        }
        emit TokenSupport(token, supported);
    }
    
    /**
     * @notice Get user account details
     */
    function getUserAccount(address user) external view returns (
        uint256 dailySpendingLimit,
        uint256 totalSpendingCap,
        uint256 accountCreated,
        bool isActive,
        uint256 refundableAmount
    ) {
        UserAccount storage account = userAccounts[user];
        return (
            account.dailySpendingLimit,
            account.totalSpendingCap,
            account.accountCreated,
            account.isActive,
            account.refundableAmount
        );
    }
    
    /**
     * @notice Get user token balance
     */
    function getUserTokenBalance(address user, address token) external view returns (uint256) {
        return userAccounts[user].tokenBalances[token];
    }
    
    /**
     * @notice Get user service usage for today
     */
    function getUserDailyUsage(address user, ServiceType serviceType) external view returns (uint256) {
        return userAccounts[user].dailyUsage[serviceType];
    }
    
    /**
     * @notice Check if user can access service
     */
    function canAccessService(address user, ServiceType serviceType) external view returns (bool, string memory) {
        if (!userAccounts[user].isActive) return (false, "Account not active");
        if (!serviceConfigs[serviceType].isActive) return (false, "Service not active");
        
        // Check gPFORK staking requirement
        uint256 stakedAmount = gPforkToken.balanceOf(user);
        if (stakedAmount < serviceConfigs[serviceType].minimumStakeRequired) {
            return (false, "Insufficient gPFORK tokens (staked PFORK)");
        }
        
        // Check daily limits
        if (userAccounts[user].dailyUsage[serviceType] >= serviceConfigs[serviceType].maxQueriesPerDay) {
            return (false, "Daily query limit reached");
        }
        
        return (true, "Access granted");
    }
    
    /**
     * @notice Emergency pause
     */
    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }
    
    /**
     * @notice Unpause
     */
    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }
    
    // Internal functions
    function _calculateServiceCost(address user, ServiceType serviceType) internal view returns (uint256) {
        uint256 baseCost = serviceConfigs[serviceType].baseCostPerQuery;
        
        // Apply staker discount based on gPFORK balance
        uint256 stakedAmount = gPforkToken.balanceOf(user);
        if (stakedAmount > 0) {
            uint256 discount = serviceConfigs[serviceType].discountForStakers;
            baseCost = baseCost * (10000 - discount) / 10000;
        }
        
        return baseCost;
    }
    
    function _resetDailyUsageIfNeeded(address user, ServiceType serviceType) internal {
        if (block.timestamp >= userAccounts[user].lastUsageReset[serviceType] + 1 days) {
            userAccounts[user].dailyUsage[serviceType] = 0;
            userAccounts[user].lastUsageReset[serviceType] = block.timestamp;
        }
    }
    
    function _initializeServices() internal {
        // Initialize service configurations
        string[8] memory services = [
            "Consciousness Analysis",
            "Strategic Insights", 
            "Decision Synthesis",
            "Pattern Recognition",
            "Wisdom Integration",
            "Crisis Management",
            "Leadership Coaching",
            "Reflection Guidance"
        ];
        
        for (uint256 i = 0; i < 8; i++) {
            ServiceConfig storage config = serviceConfigs[ServiceType(i)];
            config.baseCostPerQuery = (i + 1) * 10**16; // 0.01 ETH base, increasing
            config.maxQueriesPerDay = 100 - (i * 10); // Decreasing limits for premium services
            config.discountForStakers = 1000 + (i * 500); // 10-45% discount range
            config.isActive = true;
            config.serviceName = services[i];
            config.minimumStakeRequired = (i + 1) * 100 * 10**18; // 100-800 gPFORK staking requirement
        }
    }
    
    function _setupSupportedTokens() internal {
        // ETH support
        supportedTokens[address(0)] = true;
        tokenNames[address(0)] = "Ethereum";
        tokenDecimals[address(0)] = 18;
        
        // PFORK token support
        supportedTokens[address(pforkToken)] = true;
        tokenNames[address(pforkToken)] = "Pitchfork Token";
        tokenDecimals[address(pforkToken)] = 18;
    }
}

contracts/ConsciousnessIdentity.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/cryptography/MessageHashUtils.sol";
import "./interfaces/IConsciousnessAchievements.sol";

/**
 * @title ConsciousnessIdentity
 * @notice Decentralized identity and verification system for consciousness profiles
 * @dev Implements self-sovereign identity, credential verification, and privacy-preserving sharing
 */
contract ConsciousnessIdentity is AccessControl, ReentrancyGuard, Pausable {
    using ECDSA for bytes32;
    using MessageHashUtils for bytes32;
    
    bytes32 public constant VERIFIER_ROLE = keccak256("VERIFIER_ROLE");
    bytes32 public constant CREDENTIAL_ISSUER_ROLE = keccak256("CREDENTIAL_ISSUER_ROLE");
    bytes32 public constant ENTERPRISE_ADMIN_ROLE = keccak256("ENTERPRISE_ADMIN_ROLE");
    bytes32 public constant PRIVACY_GUARDIAN_ROLE = keccak256("PRIVACY_GUARDIAN_ROLE");
    
    // Identity structures
    enum VerificationLevel {
        NONE,
        BASIC,
        EXECUTIVE,
        ENTERPRISE,
        CONSCIOUSNESS_VERIFIED,
        THOUGHT_LEADER
    }
    
    enum CredentialType {
        LEADERSHIP_ASSESSMENT,
        EXECUTIVE_EXPERIENCE,
        CONSCIOUSNESS_SCORE,
        STRATEGIC_THINKING,
        CRISIS_MANAGEMENT,
        TEAM_LEADERSHIP,
        INNOVATION_CAPACITY,
        ETHICAL_DECISION_MAKING,
        WISDOM_APPLICATION,
        TRANSFORMATION_LEADERSHIP
    }
    
    struct Identity {
        address owner;
        VerificationLevel verificationLevel;
        uint256 consciousnessScore;
        uint256 lastScoreUpdate;
        string publicProfile; // IPFS hash for public information
        bytes32 privateProfileHash; // Hash of private profile for verification
        bool isEnterpriseAccount;
        uint256 createdAt;
        uint256 lastUpdated;
        mapping(CredentialType => Credential) credentials;
        mapping(address => bool) authorizedViewers; // Who can view private credentials
        mapping(bytes32 => bool) sharedCredentials; // Specific credentials shared
    }
    
    struct Credential {
        CredentialType credType;
        address issuer;
        uint256 score;
        uint256 issuedAt;
        uint256 expiresAt;
        bool isVerified;
        bool isActive;
        string metadataURI; // IPFS hash for additional data
        bytes32 proofHash; // Hash of verification proof
        mapping(address => bool) endorsements; // Peer endorsements
    }
    
    struct VerificationAttestation {
        address subject;
        address verifier;
        VerificationLevel level;
        string evidence; // IPFS hash of verification documents
        uint256 timestamp;
        bool isValid;
        bytes signature;
    }
    
    struct PrivacySettings {
        bool allowPublicProfile;
        bool allowCredentialSharing;
        bool allowConsciousnessScoring;
        bool allowEnterpriseAccess;
        mapping(CredentialType => bool) publicCredentials;
        mapping(address => mapping(CredentialType => bool)) sharedWith;
    }
    
    // State variables
    mapping(address => Identity) public identities;
    mapping(address => PrivacySettings) public privacySettings;
    mapping(bytes32 => VerificationAttestation) public attestations;
    mapping(address => bool) public trustedIssuers;
    mapping(address => uint256) public verificationScores;
    mapping(CredentialType => uint256) public minimumScoresRequired;
    
    IConsciousnessAchievements public achievementsContract;
    
    // Platform settings
    uint256 public constant MAX_CONSCIOUSNESS_SCORE = 1000;
    uint256 public verificationFee = 10 * 10**18; // 10 CONS tokens
    uint256 public credentialValidityPeriod = 365 days;
    uint256 public attestationValidityPeriod = 90 days;
    
    // Merkle tree for privacy-preserving verification
    mapping(bytes32 => bool) public merkleRoots;
    mapping(address => bytes32) public userMerkleRoots;
    
    // Events
    event IdentityCreated(address indexed user, VerificationLevel level);
    event IdentityUpdated(address indexed user, VerificationLevel level);
    event CredentialIssued(address indexed user, CredentialType credType, address indexed issuer);
    event CredentialVerified(address indexed user, CredentialType credType, address indexed verifier);
    event AttestationCreated(bytes32 indexed attestationId, address indexed subject, address indexed verifier);
    event ConsciousnessScoreUpdated(address indexed user, uint256 newScore);
    event CredentialShared(address indexed user, address indexed viewer, CredentialType credType);
    event PrivacySettingsUpdated(address indexed user);
    event VerificationLevelUpgraded(address indexed user, VerificationLevel oldLevel, VerificationLevel newLevel);
    
    constructor(
        address _achievementsContract
    ) {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(VERIFIER_ROLE, msg.sender);
        _grantRole(CREDENTIAL_ISSUER_ROLE, msg.sender);
        _grantRole(ENTERPRISE_ADMIN_ROLE, msg.sender);
        _grantRole(PRIVACY_GUARDIAN_ROLE, msg.sender);
        
        achievementsContract = IConsciousnessAchievements(_achievementsContract);
        
        _initializeMinimumScores();
    }
    
    /**
     * @notice Create a new decentralized identity
     * @param publicProfile IPFS hash of public profile information
     * @param privateProfileHash Hash of private profile for verification
     * @param isEnterpriseAccount Whether this is an enterprise account
     */
    function createIdentity(
        string memory publicProfile,
        bytes32 privateProfileHash,
        bool isEnterpriseAccount
    ) external whenNotPaused {
        require(identities[msg.sender].owner == address(0), "Identity already exists");
        
        Identity storage identity = identities[msg.sender];
        identity.owner = msg.sender;
        identity.verificationLevel = VerificationLevel.BASIC;
        identity.consciousnessScore = 0;
        identity.lastScoreUpdate = block.timestamp;
        identity.publicProfile = publicProfile;
        identity.privateProfileHash = privateProfileHash;
        identity.isEnterpriseAccount = isEnterpriseAccount;
        identity.createdAt = block.timestamp;
        identity.lastUpdated = block.timestamp;
        
        // Initialize default privacy settings
        PrivacySettings storage privacy = privacySettings[msg.sender];
        privacy.allowPublicProfile = true;
        privacy.allowCredentialSharing = false;
        privacy.allowConsciousnessScoring = true;
        privacy.allowEnterpriseAccess = isEnterpriseAccount;
        
        emit IdentityCreated(msg.sender, VerificationLevel.BASIC);
    }
    
    /**
     * @notice Issue a credential to a user
     * @param user User to issue credential to
     * @param credType Type of credential
     * @param score Credential score
     * @param metadataURI IPFS hash for additional metadata
     * @param proofHash Hash of verification proof
     */
    function issueCredential(
        address user,
        CredentialType credType,
        uint256 score,
        string memory metadataURI,
        bytes32 proofHash
    ) external onlyRole(CREDENTIAL_ISSUER_ROLE) {
        require(identities[user].owner != address(0), "Identity does not exist");
        require(score <= MAX_CONSCIOUSNESS_SCORE, "Score exceeds maximum");
        
        Credential storage credential = identities[user].credentials[credType];
        credential.credType = credType;
        credential.issuer = msg.sender;
        credential.score = score;
        credential.issuedAt = block.timestamp;
        credential.expiresAt = block.timestamp + credentialValidityPeriod;
        credential.isVerified = false;
        credential.isActive = true;
        credential.metadataURI = metadataURI;
        credential.proofHash = proofHash;
        
        identities[user].lastUpdated = block.timestamp;
        
        emit CredentialIssued(user, credType, msg.sender);
    }
    
    /**
     * @notice Verify a user's credential
     * @param user User whose credential to verify
     * @param credType Type of credential to verify
     * @param evidence IPFS hash of verification evidence
     */
    function verifyCredential(
        address user,
        CredentialType credType,
        string memory evidence
    ) external onlyRole(VERIFIER_ROLE) {
        require(identities[user].owner != address(0), "Identity does not exist");
        
        Credential storage credential = identities[user].credentials[credType];
        require(credential.isActive, "Credential not active");
        require(block.timestamp <= credential.expiresAt, "Credential expired");
        
        credential.isVerified = true;
        
        // Update consciousness score based on verified credentials
        _updateConsciousnessScore(user);
        
        // Check for verification level upgrade
        _checkVerificationLevelUpgrade(user);
        
        emit CredentialVerified(user, credType, msg.sender);
    }
    
    /**
     * @notice Create verification attestation
     * @param subject Address of the subject being attested
     * @param level Verification level being attested
     * @param evidence IPFS hash of evidence
     * @param signature Verifier's signature
     */
    function createAttestation(
        address subject,
        VerificationLevel level,
        string memory evidence,
        bytes memory signature
    ) external onlyRole(VERIFIER_ROLE) returns (bytes32 attestationId) {
        require(identities[subject].owner != address(0), "Subject identity does not exist");
        
        attestationId = keccak256(abi.encodePacked(subject, msg.sender, level, block.timestamp));
        
        VerificationAttestation storage attestation = attestations[attestationId];
        attestation.subject = subject;
        attestation.verifier = msg.sender;
        attestation.level = level;
        attestation.evidence = evidence;
        attestation.timestamp = block.timestamp;
        attestation.isValid = true;
        attestation.signature = signature;
        
        verificationScores[subject] += uint256(level) * 100;
        
        emit AttestationCreated(attestationId, subject, msg.sender);
        
        return attestationId;
    }
    
    /**
     * @notice Update consciousness score
     * @param user User to update score for
     * @param newScore New consciousness score
     * @param assessmentData IPFS hash of assessment data
     */
    function updateConsciousnessScore(
        address user,
        uint256 newScore,
        string memory assessmentData
    ) external onlyRole(CREDENTIAL_ISSUER_ROLE) {
        require(identities[user].owner != address(0), "Identity does not exist");
        require(newScore <= MAX_CONSCIOUSNESS_SCORE, "Score exceeds maximum");
        require(privacySettings[user].allowConsciousnessScoring, "Consciousness scoring not allowed");
        
        uint256 oldScore = identities[user].consciousnessScore;
        identities[user].consciousnessScore = newScore;
        identities[user].lastScoreUpdate = block.timestamp;
        identities[user].lastUpdated = block.timestamp;
        
        // Update achievements contract
        achievementsContract.updateConsciousnessScore(user, newScore);
        
        // Check for verification level upgrade
        _checkVerificationLevelUpgrade(user);
        
        emit ConsciousnessScoreUpdated(user, newScore);
    }
    
    /**
     * @notice Share credential with specific address
     * @param viewer Address to share credential with
     * @param credType Type of credential to share
     * @param allowAccess Whether to grant or revoke access
     */
    function shareCredential(
        address viewer,
        CredentialType credType,
        bool allowAccess
    ) external {
        require(identities[msg.sender].owner != address(0), "Identity does not exist");
        require(privacySettings[msg.sender].allowCredentialSharing, "Credential sharing disabled");
        
        privacySettings[msg.sender].sharedWith[viewer][credType] = allowAccess;
        
        if (allowAccess) {
            emit CredentialShared(msg.sender, viewer, credType);
        }
    }
    
    /**
     * @notice Update privacy settings
     * @param allowPublicProfile Allow public profile viewing
     * @param allowCredentialSharing Allow credential sharing
     * @param allowConsciousnessScoring Allow consciousness score updates
     * @param allowEnterpriseAccess Allow enterprise account access
     */
    function updatePrivacySettings(
        bool allowPublicProfile,
        bool allowCredentialSharing,
        bool allowConsciousnessScoring,
        bool allowEnterpriseAccess
    ) external {
        require(identities[msg.sender].owner != address(0), "Identity does not exist");
        
        PrivacySettings storage privacy = privacySettings[msg.sender];
        privacy.allowPublicProfile = allowPublicProfile;
        privacy.allowCredentialSharing = allowCredentialSharing;
        privacy.allowConsciousnessScoring = allowConsciousnessScoring;
        privacy.allowEnterpriseAccess = allowEnterpriseAccess;
        
        emit PrivacySettingsUpdated(msg.sender);
    }
    
    /**
     * @notice Set credential as public or private
     * @param credType Type of credential
     * @param isPublic Whether credential should be public
     */
    function setCredentialVisibility(CredentialType credType, bool isPublic) external {
        require(identities[msg.sender].owner != address(0), "Identity does not exist");
        privacySettings[msg.sender].publicCredentials[credType] = isPublic;
    }
    
    /**
     * @notice Get user's identity information
     * @param user User to get identity for
     */
    function getIdentity(address user) external view returns (
        VerificationLevel verificationLevel,
        uint256 consciousnessScore,
        uint256 lastScoreUpdate,
        string memory publicProfile,
        bool isEnterpriseAccount,
        uint256 createdAt,
        uint256 lastUpdated
    ) {
        Identity storage identity = identities[user];
        require(identity.owner != address(0), "Identity does not exist");
        
        return (
            identity.verificationLevel,
            identity.consciousnessScore,
            identity.lastScoreUpdate,
            identity.publicProfile,
            identity.isEnterpriseAccount,
            identity.createdAt,
            identity.lastUpdated
        );
    }
    
    /**
     * @notice Get user's credential information
     * @param user User to get credential for
     * @param credType Type of credential
     */
    function getCredential(address user, CredentialType credType) external view returns (
        address issuer,
        uint256 score,
        uint256 issuedAt,
        uint256 expiresAt,
        bool isVerified,
        bool isActive,
        string memory metadataURI
    ) {
        require(identities[user].owner != address(0), "Identity does not exist");
        require(_canViewCredential(msg.sender, user, credType), "Access denied");
        
        Credential storage credential = identities[user].credentials[credType];
        
        return (
            credential.issuer,
            credential.score,
            credential.issuedAt,
            credential.expiresAt,
            credential.isVerified,
            credential.isActive,
            credential.metadataURI
        );
    }
    
    /**
     * @notice Check if address can view user's credentials
     * @param viewer Address requesting access
     * @param user User whose credentials are being accessed
     * @param credType Type of credential
     */
    function canViewCredential(address viewer, address user, CredentialType credType) external view returns (bool) {
        return _canViewCredential(viewer, user, credType);
    }
    
    /**
     * @notice Verify attestation signature
     * @param attestationId ID of attestation to verify
     */
    function verifyAttestation(bytes32 attestationId) external view returns (bool) {
        VerificationAttestation storage attestation = attestations[attestationId];
        require(attestation.subject != address(0), "Attestation does not exist");
        require(attestation.isValid, "Attestation is not valid");
        require(block.timestamp <= attestation.timestamp + attestationValidityPeriod, "Attestation expired");
        
        bytes32 message = keccak256(abi.encodePacked(
            attestation.subject,
            attestation.level,
            attestation.evidence,
            attestation.timestamp
        ));
        
        bytes32 ethSignedMessageHash = message.toEthSignedMessageHash();
        address recoveredSigner = ethSignedMessageHash.recover(attestation.signature);
        
        return recoveredSigner == attestation.verifier && hasRole(VERIFIER_ROLE, attestation.verifier);
    }
    
    /**
     * @notice Set trusted issuer status
     * @param issuer Address of issuer
     * @param trusted Whether issuer is trusted
     */
    function setTrustedIssuer(address issuer, bool trusted) external onlyRole(DEFAULT_ADMIN_ROLE) {
        trustedIssuers[issuer] = trusted;
    }
    
    /**
     * @notice Emergency pause
     */
    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }
    
    /**
     * @notice Unpause
     */
    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }
    
    // Internal functions
    function _canViewCredential(address viewer, address user, CredentialType credType) internal view returns (bool) {
        if (viewer == user) return true; // User can always view own credentials
        
        PrivacySettings storage privacy = privacySettings[user];
        
        // Check if credential is public
        if (privacy.publicCredentials[credType]) return true;
        
        // Check if viewer has been granted access
        if (privacy.sharedWith[viewer][credType]) return true;
        
        // Check if viewer has enterprise admin role and user allows enterprise access
        if (hasRole(ENTERPRISE_ADMIN_ROLE, viewer) && privacy.allowEnterpriseAccess) return true;
        
        // Check if viewer is a trusted issuer
        if (trustedIssuers[viewer]) return true;
        
        return false;
    }
    
    function _updateConsciousnessScore(address user) internal {
        uint256 totalScore = 0;
        uint256 verifiedCredentials = 0;
        
        for (uint256 i = 0; i < 10; i++) { // 10 credential types
            CredentialType credType = CredentialType(i);
            Credential storage credential = identities[user].credentials[credType];
            
            if (credential.isVerified && credential.isActive && block.timestamp <= credential.expiresAt) {
                totalScore += credential.score;
                verifiedCredentials++;
            }
        }
        
        if (verifiedCredentials > 0) {
            uint256 averageScore = totalScore / verifiedCredentials;
            identities[user].consciousnessScore = averageScore;
            identities[user].lastScoreUpdate = block.timestamp;
        }
    }
    
    function _checkVerificationLevelUpgrade(address user) internal {
        Identity storage identity = identities[user];
        uint256 score = identity.consciousnessScore;
        uint256 verificationScore = verificationScores[user];
        
        VerificationLevel oldLevel = identity.verificationLevel;
        VerificationLevel newLevel = oldLevel;
        
        if (score >= 800 && verificationScore >= 500) {
            newLevel = VerificationLevel.THOUGHT_LEADER;
        } else if (score >= 700 && verificationScore >= 400) {
            newLevel = VerificationLevel.CONSCIOUSNESS_VERIFIED;
        } else if (score >= 600 && verificationScore >= 300) {
            newLevel = VerificationLevel.ENTERPRISE;
        } else if (score >= 400 && verificationScore >= 200) {
            newLevel = VerificationLevel.EXECUTIVE;
        } else if (score >= 200) {
            newLevel = VerificationLevel.BASIC;
        }
        
        if (newLevel > oldLevel) {
            identity.verificationLevel = newLevel;
            identity.lastUpdated = block.timestamp;
            emit VerificationLevelUpgraded(user, oldLevel, newLevel);
        }
    }
    
    function _initializeMinimumScores() internal {
        minimumScoresRequired[CredentialType.LEADERSHIP_ASSESSMENT] = 300;
        minimumScoresRequired[CredentialType.EXECUTIVE_EXPERIENCE] = 400;
        minimumScoresRequired[CredentialType.CONSCIOUSNESS_SCORE] = 200;
        minimumScoresRequired[CredentialType.STRATEGIC_THINKING] = 350;
        minimumScoresRequired[CredentialType.CRISIS_MANAGEMENT] = 450;
        minimumScoresRequired[CredentialType.TEAM_LEADERSHIP] = 300;
        minimumScoresRequired[CredentialType.INNOVATION_CAPACITY] = 400;
        minimumScoresRequired[CredentialType.ETHICAL_DECISION_MAKING] = 500;
        minimumScoresRequired[CredentialType.WISDOM_APPLICATION] = 600;
        minimumScoresRequired[CredentialType.TRANSFORMATION_LEADERSHIP] = 700;
    }
}

contracts/interfaces/IConsciousnessAchie.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";

/**
 * @title IConsciousnessAchievements
 * @notice Interface for the Consciousness Achievements NFT contract
 */
interface IConsciousnessAchievements is IERC721 {
    enum AchievementCategory {
        CONSCIOUS_LEADER,
        STRATEGIC_THINKER,
        CRISIS_MANAGER,
        INNOVATION_CATALYST,
        ETHICAL_GUARDIAN,
        WISDOM_KEEPER,
        TRANSFORMATION_AGENT,
        COLLABORATION_MASTER
    }
    
    enum AchievementLevel {
        BRONZE,
        SILVER,
        GOLD,
        PLATINUM,
        DIAMOND,
        TRANSCENDENT
    }
    
    // Events
    event AchievementMinted(uint256 indexed tokenId, address indexed recipient, AchievementCategory category, AchievementLevel level);
    event AchievementEvolved(uint256 indexed tokenId, AchievementLevel oldLevel, AchievementLevel newLevel);
    event ConsciousnessScoreUpdated(address indexed user, uint256 newScore);
    event SkillUnlocked(address indexed user, string skill, uint256 level);
    event MetadataUpdated(uint256 indexed tokenId, string newMetadataURI);
    
    // Core functions
    function mintAchievement(
        address to,
        AchievementCategory category,
        AchievementLevel level,
        uint256 consciousnessScore,
        uint256 assessmentScore,
        string[] memory skills
    ) external returns (uint256);
    
    function evolveAchievement(uint256 tokenId) external;
    
    // View functions
    function getAchievement(uint256 tokenId) external view returns (
        AchievementCategory category,
        AchievementLevel level,
        uint256 issuedAt,
        uint256 lastEvolution,
        address recipient,
        string memory metadataURI,
        uint256 consciousnessScore,
        uint256 assessmentScore,
        string[] memory skillsUnlocked
    );
    
    function getUserAchievements(address user) external view returns (uint256[] memory);
    function canEvolveAchievement(uint256 tokenId) external view returns (bool, string memory reason);
    function userConsciousnessScores(address user) external view returns (uint256);
    function userSkillLevels(address user, string memory skill) external view returns (uint256);
    
    // Admin functions
    function updateConsciousnessScore(address user, uint256 newScore) external;
    function updateSkillLevel(address user, string memory skill, uint256 level) external;
}

contracts/LeadershipSubscription.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./interfaces/IConsciousnessAchievements.sol";

/**
 * @title LeadershipSubscription
 * @notice Manages tiered subscriptions for leadership development features
 * @dev Implements automatic renewals, governance rights, and revenue distribution
 */
contract LeadershipSubscription is AccessControl, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;
    
    bytes32 public constant SUBSCRIPTION_MANAGER_ROLE = keccak256("SUBSCRIPTION_MANAGER_ROLE");
    bytes32 public constant REVENUE_MANAGER_ROLE = keccak256("REVENUE_MANAGER_ROLE");
    bytes32 public constant GOVERNANCE_ADMIN_ROLE = keccak256("GOVERNANCE_ADMIN_ROLE");
    
    // Subscription tiers
    enum SubscriptionTier {
        NONE,
        BASIC,
        PREMIUM,
        ENTERPRISE
    }
    
    // Subscription structures
    struct TierConfig {
        uint256 monthlyPrice;
        uint256 annualPrice;
        uint256 maxAIQueries;
        uint256 maxAssessments;
        uint256 votingPower;
        bool hasAdvancedAnalytics;
        bool hasPersonalCoaching;
        bool hasAPIAccess;
        bool hasWhiteLabel;
        string[] includedFeatures;
        uint256 consciousnessRequirement; // Minimum consciousness score
        bool isActive;
    }
    
    struct Subscription {
        SubscriptionTier tier;
        uint256 startTime;
        uint256 endTime;
        bool isAnnual;
        bool autoRenewal;
        uint256 lastPayment;
        uint256 totalPaid;
        address paymentToken;
        bool isActive;
        uint256 votingPowerAllocated;
    }
    
    struct RevenueShare {
        address stakeholder;
        uint256 percentage; // Basis points (e.g., 500 = 5%)
        string role;
        bool isActive;
    }
    
    // State variables
    mapping(SubscriptionTier => TierConfig) public tierConfigs;
    mapping(address => Subscription) public subscriptions;
    mapping(address => bool) public supportedPaymentTokens;
    mapping(address => RevenueShare) public revenueShares;
    
    IERC20 public pforkToken;
    IConsciousnessAchievements public achievementContract;
    
    address[] public stakeholders;
    uint256 public totalVotingPower;
    uint256 public totalSubscribers;
    uint256 public totalRevenue;
    
    // Platform settings
    uint256 public constant BASIS_POINTS = 10000;
    uint256 public constant MONTH_DURATION = 30 days;
    uint256 public constant YEAR_DURATION = 365 days;
    uint256 public gracePeriod = 7 days;
    uint256 public renewalWindow = 3 days;
    
    // Revenue distribution
    mapping(SubscriptionTier => uint256) public tierRevenue;
    mapping(address => uint256) public pendingPayouts;
    
    // Events
    event SubscriptionCreated(address indexed user, SubscriptionTier tier, bool isAnnual, uint256 endTime);
    event SubscriptionRenewed(address indexed user, SubscriptionTier tier, uint256 newEndTime);
    event SubscriptionUpgraded(address indexed user, SubscriptionTier oldTier, SubscriptionTier newTier);
    event SubscriptionCancelled(address indexed user, SubscriptionTier tier);
    event AutoRenewalToggled(address indexed user, bool enabled);
    event TierConfigUpdated(SubscriptionTier tier, uint256 monthlyPrice, uint256 annualPrice);
    event RevenueDistributed(uint256 totalAmount, uint256 timestamp);
    event VotingPowerAllocated(address indexed user, uint256 power);
    event PaymentTokenUpdated(address indexed token, bool supported);
    
    constructor(
        address _pforkToken,
        address _achievementContract
    ) {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(SUBSCRIPTION_MANAGER_ROLE, msg.sender);
        _grantRole(REVENUE_MANAGER_ROLE, msg.sender);
        _grantRole(GOVERNANCE_ADMIN_ROLE, msg.sender);
        
        pforkToken = IERC20(_pforkToken);
        achievementContract = IConsciousnessAchievements(_achievementContract);
        
        _initializeTierConfigs();
        _setupRevenueShares();
    }
    
    /**
     * @notice Subscribe to a leadership development tier
     * @param tier Subscription tier to purchase
     * @param isAnnual Whether to pay annually (gets discount)
     * @param paymentToken Token to pay with
     * @param enableAutoRenewal Whether to enable automatic renewals
     */
    function subscribe(
        SubscriptionTier tier,
        bool isAnnual,
        address paymentToken,
        bool enableAutoRenewal
    ) external nonReentrant whenNotPaused {
        require(tier != SubscriptionTier.NONE, "Invalid tier");
        require(tierConfigs[tier].isActive, "Tier not available");
        require(supportedPaymentTokens[paymentToken], "Payment token not supported");
        
        // Check consciousness requirement
        uint256 userScore = achievementContract.userConsciousnessScores(msg.sender);
        require(userScore >= tierConfigs[tier].consciousnessRequirement, "Consciousness level too low");
        
        // Cancel existing subscription if active
        if (subscriptions[msg.sender].isActive) {
            _cancelSubscription(msg.sender);
        }
        
        // Calculate price and duration
        uint256 price = isAnnual ? tierConfigs[tier].annualPrice : tierConfigs[tier].monthlyPrice;
        uint256 duration = isAnnual ? YEAR_DURATION : MONTH_DURATION;
        
        // Process payment
        IERC20(paymentToken).safeTransferFrom(msg.sender, address(this), price);
        
        // Create subscription
        Subscription storage sub = subscriptions[msg.sender];
        sub.tier = tier;
        sub.startTime = block.timestamp;
        sub.endTime = block.timestamp + duration;
        sub.isAnnual = isAnnual;
        sub.autoRenewal = enableAutoRenewal;
        sub.lastPayment = block.timestamp;
        sub.totalPaid = price;
        sub.paymentToken = paymentToken;
        sub.isActive = true;
        
        // Allocate voting power
        uint256 votingPower = tierConfigs[tier].votingPower;
        sub.votingPowerAllocated = votingPower;
        totalVotingPower += votingPower;
        
        // Update counters
        totalSubscribers++;
        totalRevenue += price;
        tierRevenue[tier] += price;
        
        // Distribute revenue
        _distributeRevenue(price);
        
        emit SubscriptionCreated(msg.sender, tier, isAnnual, sub.endTime);
        emit VotingPowerAllocated(msg.sender, votingPower);
    }
    
    /**
     * @notice Upgrade subscription to higher tier
     * @param newTier New subscription tier
     */
    function upgradeSubscription(SubscriptionTier newTier) external nonReentrant {
        require(newTier > subscriptions[msg.sender].tier, "Not an upgrade");
        require(subscriptions[msg.sender].isActive, "No active subscription");
        require(tierConfigs[newTier].isActive, "Tier not available");
        
        Subscription storage sub = subscriptions[msg.sender];
        SubscriptionTier oldTier = sub.tier;
        
        // Calculate prorated price difference
        uint256 remainingTime = sub.endTime > block.timestamp ? sub.endTime - block.timestamp : 0;
        require(remainingTime > 0, "Subscription expired");
        
        uint256 oldTierPrice = sub.isAnnual ? tierConfigs[oldTier].annualPrice : tierConfigs[oldTier].monthlyPrice;
        uint256 newTierPrice = sub.isAnnual ? tierConfigs[newTier].annualPrice : tierConfigs[newTier].monthlyPrice;
        uint256 duration = sub.isAnnual ? YEAR_DURATION : MONTH_DURATION;
        
        uint256 proratedDifference = ((newTierPrice - oldTierPrice) * remainingTime) / duration;
        
        // Process upgrade payment
        IERC20(sub.paymentToken).safeTransferFrom(msg.sender, address(this), proratedDifference);
        
        // Update subscription
        uint256 oldVotingPower = sub.votingPowerAllocated;
        uint256 newVotingPower = tierConfigs[newTier].votingPower;
        
        sub.tier = newTier;
        sub.totalPaid += proratedDifference;
        sub.votingPowerAllocated = newVotingPower;
        
        // Update voting power
        totalVotingPower = totalVotingPower - oldVotingPower + newVotingPower;
        
        // Update revenue
        totalRevenue += proratedDifference;
        tierRevenue[newTier] += proratedDifference;
        
        _distributeRevenue(proratedDifference);
        
        emit SubscriptionUpgraded(msg.sender, oldTier, newTier);
        emit VotingPowerAllocated(msg.sender, newVotingPower);
    }
    
    /**
     * @notice Process automatic renewal for eligible subscriptions
     * @param user User to renew subscription for
     */
    function processAutoRenewal(address user) external onlyRole(SUBSCRIPTION_MANAGER_ROLE) {
        Subscription storage sub = subscriptions[user];
        require(sub.isActive, "No active subscription");
        require(sub.autoRenewal, "Auto renewal not enabled");
        require(block.timestamp >= sub.endTime - renewalWindow, "Too early for renewal");
        require(block.timestamp <= sub.endTime + gracePeriod, "Grace period expired");
        
        uint256 price = sub.isAnnual ? tierConfigs[sub.tier].annualPrice : tierConfigs[sub.tier].monthlyPrice;
        uint256 duration = sub.isAnnual ? YEAR_DURATION : MONTH_DURATION;
        
        // Check user's token balance and allowance
        IERC20 paymentToken = IERC20(sub.paymentToken);
        require(paymentToken.balanceOf(user) >= price, "Insufficient balance for renewal");
        require(paymentToken.allowance(user, address(this)) >= price, "Insufficient allowance for renewal");
        
        // Process renewal payment
        paymentToken.safeTransferFrom(user, address(this), price);
        
        // Extend subscription
        sub.endTime = block.timestamp + duration;
        sub.lastPayment = block.timestamp;
        sub.totalPaid += price;
        
        // Update revenue
        totalRevenue += price;
        tierRevenue[sub.tier] += price;
        
        _distributeRevenue(price);
        
        emit SubscriptionRenewed(user, sub.tier, sub.endTime);
    }
    
    /**
     * @notice Cancel subscription and remove voting power
     */
    function cancelSubscription() external {
        require(subscriptions[msg.sender].isActive, "No active subscription");
        _cancelSubscription(msg.sender);
        emit SubscriptionCancelled(msg.sender, subscriptions[msg.sender].tier);
    }
    
    /**
     * @notice Toggle auto-renewal for subscription
     */
    function toggleAutoRenewal(bool enabled) external {
        require(subscriptions[msg.sender].isActive, "No active subscription");
        subscriptions[msg.sender].autoRenewal = enabled;
        emit AutoRenewalToggled(msg.sender, enabled);
    }
    
    /**
     * @notice Check if user has access to specific features
     * @param user User to check
     * @param feature Feature name to check access for
     */
    function hasFeatureAccess(address user, string memory feature) external view returns (bool) {
        if (!isSubscriptionActive(user)) return false;
        
        SubscriptionTier tier = subscriptions[user].tier;
        TierConfig storage config = tierConfigs[tier];
        
        // Check tier-specific features
        if (keccak256(bytes(feature)) == keccak256(bytes("advanced_analytics"))) {
            return config.hasAdvancedAnalytics;
        }
        if (keccak256(bytes(feature)) == keccak256(bytes("personal_coaching"))) {
            return config.hasPersonalCoaching;
        }
        if (keccak256(bytes(feature)) == keccak256(bytes("api_access"))) {
            return config.hasAPIAccess;
        }
        if (keccak256(bytes(feature)) == keccak256(bytes("white_label"))) {
            return config.hasWhiteLabel;
        }
        
        // Check included features
        for (uint256 i = 0; i < config.includedFeatures.length; i++) {
            if (keccak256(bytes(config.includedFeatures[i])) == keccak256(bytes(feature))) {
                return true;
            }
        }
        
        return false;
    }
    
    /**
     * @notice Check if user's subscription is currently active
     */
    function isSubscriptionActive(address user) public view returns (bool) {
        Subscription storage sub = subscriptions[user];
        return sub.isActive && block.timestamp <= sub.endTime + gracePeriod;
    }
    
    /**
     * @notice Get user's current voting power
     */
    function getVotingPower(address user) external view returns (uint256) {
        if (!isSubscriptionActive(user)) return 0;
        return subscriptions[user].votingPowerAllocated;
    }
    
    /**
     * @notice Get subscription details for user
     */
    function getSubscription(address user) external view returns (
        SubscriptionTier tier,
        uint256 startTime,
        uint256 endTime,
        bool isAnnual,
        bool autoRenewal,
        uint256 totalPaid,
        bool isActive,
        uint256 votingPower
    ) {
        Subscription storage sub = subscriptions[user];
        return (
            sub.tier,
            sub.startTime,
            sub.endTime,
            sub.isAnnual,
            sub.autoRenewal,
            sub.totalPaid,
            isSubscriptionActive(user),
            sub.votingPowerAllocated
        );
    }
    
    /**
     * @notice Update tier configuration
     */
    function updateTierConfig(
        SubscriptionTier tier,
        uint256 monthlyPrice,
        uint256 annualPrice,
        uint256 maxAIQueries,
        uint256 maxAssessments,
        uint256 votingPower,
        bool hasAdvancedAnalytics,
        bool hasPersonalCoaching,
        bool hasAPIAccess,
        bool hasWhiteLabel,
        uint256 consciousnessRequirement,
        bool isActive
    ) external onlyRole(SUBSCRIPTION_MANAGER_ROLE) {
        TierConfig storage config = tierConfigs[tier];
        config.monthlyPrice = monthlyPrice;
        config.annualPrice = annualPrice;
        config.maxAIQueries = maxAIQueries;
        config.maxAssessments = maxAssessments;
        config.votingPower = votingPower;
        config.hasAdvancedAnalytics = hasAdvancedAnalytics;
        config.hasPersonalCoaching = hasPersonalCoaching;
        config.hasAPIAccess = hasAPIAccess;
        config.hasWhiteLabel = hasWhiteLabel;
        config.consciousnessRequirement = consciousnessRequirement;
        config.isActive = isActive;
        
        emit TierConfigUpdated(tier, monthlyPrice, annualPrice);
    }
    
    /**
     * @notice Set payment token support
     */
    function setSupportedPaymentToken(address token, bool supported) 
        external 
        onlyRole(DEFAULT_ADMIN_ROLE) 
    {
        supportedPaymentTokens[token] = supported;
        emit PaymentTokenUpdated(token, supported);
    }
    
    /**
     * @notice Update revenue share for stakeholder
     */
    function updateRevenueShare(
        address stakeholder,
        uint256 percentage,
        string memory role,
        bool isActive
    ) external onlyRole(REVENUE_MANAGER_ROLE) {
        require(percentage <= BASIS_POINTS, "Invalid percentage");
        
        if (revenueShares[stakeholder].stakeholder == address(0)) {
            stakeholders.push(stakeholder);
        }
        
        revenueShares[stakeholder] = RevenueShare({
            stakeholder: stakeholder,
            percentage: percentage,
            role: role,
            isActive: isActive
        });
    }
    
    /**
     * @notice Claim pending revenue payout
     */
    function claimRevenuePayout() external nonReentrant {
        uint256 amount = pendingPayouts[msg.sender];
        require(amount > 0, "No pending payout");
        
        pendingPayouts[msg.sender] = 0;
        
        // PFORK token payouts
        pforkToken.transfer(msg.sender, amount);
    }
    
    /**
     * @notice Emergency pause
     */
    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }
    
    /**
     * @notice Unpause
     */
    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }
    
    // Internal functions
    function _cancelSubscription(address user) internal {
        Subscription storage sub = subscriptions[user];
        
        // Remove voting power
        totalVotingPower -= sub.votingPowerAllocated;
        sub.votingPowerAllocated = 0;
        
        // Deactivate subscription
        sub.isActive = false;
        sub.autoRenewal = false;
        
        totalSubscribers--;
    }
    
    function _distributeRevenue(uint256 amount) internal {
        for (uint256 i = 0; i < stakeholders.length; i++) {
            address stakeholder = stakeholders[i];
            RevenueShare storage share = revenueShares[stakeholder];
            
            if (share.isActive && share.percentage > 0) {
                uint256 payout = (amount * share.percentage) / BASIS_POINTS;
                pendingPayouts[stakeholder] += payout;
            }
        }
        
        emit RevenueDistributed(amount, block.timestamp);
    }
    
    function _initializeTierConfigs() internal {
        // Basic Tier
        TierConfig storage basic = tierConfigs[SubscriptionTier.BASIC];
        basic.monthlyPrice = 50 * 10**18; // 50 CONS
        basic.annualPrice = 500 * 10**18; // 500 CONS (2 months free)
        basic.maxAIQueries = 100;
        basic.maxAssessments = 5;
        basic.votingPower = 1;
        basic.hasAdvancedAnalytics = false;
        basic.hasPersonalCoaching = false;
        basic.hasAPIAccess = false;
        basic.hasWhiteLabel = false;
        basic.consciousnessRequirement = 100;
        basic.isActive = true;
        
        // Premium Tier  
        TierConfig storage premium = tierConfigs[SubscriptionTier.PREMIUM];
        premium.monthlyPrice = 150 * 10**18;
        premium.annualPrice = 1500 * 10**18;
        premium.maxAIQueries = 500;
        premium.maxAssessments = 20;
        premium.votingPower = 5;
        premium.hasAdvancedAnalytics = true;
        premium.hasPersonalCoaching = true;
        premium.hasAPIAccess = false;
        premium.hasWhiteLabel = false;
        premium.consciousnessRequirement = 300;
        premium.isActive = true;
        
        // Enterprise Tier
        TierConfig storage enterprise = tierConfigs[SubscriptionTier.ENTERPRISE];
        enterprise.monthlyPrice = 500 * 10**18;
        enterprise.annualPrice = 5000 * 10**18;
        enterprise.maxAIQueries = 2000;
        enterprise.maxAssessments = 100;
        enterprise.votingPower = 20;
        enterprise.hasAdvancedAnalytics = true;
        enterprise.hasPersonalCoaching = true;
        enterprise.hasAPIAccess = true;
        enterprise.hasWhiteLabel = true;
        enterprise.consciousnessRequirement = 500;
        enterprise.isActive = true;
    }
    
    function _setupRevenueShares() internal {
        // Platform development: 40%
        revenueShares[msg.sender] = RevenueShare({
            stakeholder: msg.sender,
            percentage: 4000,
            role: "Platform Development",
            isActive: true
        });
        stakeholders.push(msg.sender);
        
        // Note: Additional stakeholders can be added via updateRevenueShare
    }
}

